ENG_WORDS=`mktemp`
OWO_WORDS=`mktemp`

ag --nonumbers "th$" /usr/share/dict/words | sed "s/$/ /g" > $ENG_WORDS
cat $ENG_WORDS | nodejs -e "console.log(require('./index.js')(require('fs').readFileSync(0, 'utf-8')))" > $OWO_WORDS
echo 'Original | OWOified'
echo '--- | ---'
paste -d '| ' $ENG_WORDS $OWO_WORDS
